CREATE TABLE `Teacher` (`id` int NOT NULL AUTO_INCREMENT,`name` varchar(255) CHARACTER SET utf8 NULL,`phone` varchar(255) CHARACTER SET utf8 NULL,`weixinId` varchar(255) CHARACTER SET utf8 NULL,`email` varchar(255) CHARACTER SET utf8 NULL,`remark` varchar(255) CHARACTER SET utf8 NULL,`state` varchar(255) NULL,PRIMARY KEY (`id`) );